import { Grid } from '@mui/material'
import React, { useState } from 'react'

const ShowCustomers = ({customers}) => {
  const [filtered, setFiltered] = useState([...customers])
  const [search, setSearch] = useState('')

  const handleFiltered = (search) => {
    setFiltered(customers.filter(c => c.fullName.includes(search) || c.userName.includes(search)))
  }

  return (
    <>
      <input 
        type='text'
        value={search}
        onChange={(e) => handleFiltered(e.target.value)}
      />

      <Grid container rowSpacing={3} columnSpacing={0}>
        {filtered.map(f => (
            <Grid key={f.id} item xs={4} className='center-label'>
              <label key={f.id} className="adjacent" >
                <div className="">
                  {f.fullName}
                  <span className="d-block small">
                    {f.userName} 
                  </span>
                </div>
              </label>
            </Grid>
        ))}
      </Grid>
    </>
  )
}

export default ShowCustomers
