import { Grid } from '@mui/material'
import React, { useEffect, useState } from 'react'
import '../styles/NewRoutine.css'

const NewRoutine = ({ API_URL, onShow, routine, setRoutine, microcycles, setMicrocycles, sessionsDay, setSessionsDay, edit }) => {
  const handleRoutine = (key, value) => {
    let updated = {...routine}
    updated[key] = value

    setRoutine(updated)
  }

  const onSubmit = (e) => {
    e.preventDefault()

    console.log('Envía')
  }  

  return (
    <div className='ru-form-container'>
      <form onSubmit={onSubmit}>
        <div className='ru-form'>
          <div className='row-section'>
            <input 
              type='text'
              size='75'
              required={true}
              placeholder='Nombre'
              value={routine.name}
              onChange={(e) => handleRoutine('name', e.target.value)}
            />

            <textarea
              className='description-field'
              placeholder='Descripción'
              type='text'
              value={routine.description}
              onChange={(e) => handleRoutine('description', e.target.value)}
            />
          </div>

          <input
            type='submit'
            value='Guardar'
            />
        </div>
      </form>
    </div>
  )
}

export default NewRoutine
