import React, { useEffect, useState, useCallback } from 'react'

const Home = () => {
  const [index, setIndex] = useState(0)

  const [emptyExercise, setEmptyExercise] = useState(
    {
      name: '0',
      series: 0,
      minReps: 0,
      maxReps: 0,
      RIR: 0,
      rest: 0,
      exerciseId: 0,
      sessionId: 0
    }
  )

  const [exercisesSessions, setExercisesSessions] = useState([emptyExercise, 
    {
      name: '1',
      series: 0,
      minReps: 0,
      maxReps: 0,
      RIR: 0,
      rest: 0,
      exerciseId: 0,
      sessionId: 0
    }])

  const handleAdd = () => {
    setExercisesSessions([
      ...exercisesSessions,
      emptyExercise
    ])

    console.log(exercisesSessions)
  }

  const handleChange = (key, value) => {
    console.log(exercisesSessions[index])
    let updated = exercisesSessions
    updated[index][key] = value

    setExercisesSessions(updated)
  }

  useEffect(() => {
    console.log(exercisesSessions)
    console.log(index)
  }, [exercisesSessions])

  return (
    <div>
      <button onClick={() => handleAdd()}>Add blank</button>
      {exercisesSessions?.length > 0 ? (
        <form>
          <input 
            type={'text'}
            value={exercisesSessions[index].name}
            onChange={(e) => handleChange('name', e.target.value)}  
          />
          {index > 0 ? (
            <button onClick={() => setIndex(index-1)}>Anterior</button>
          ) : (
            <></>
          )}

          {exercisesSessions.length - index > 1 ? (
            <button onClick={() => setIndex(index+1)}>Siguiente</button>
          ) : (
            <></>
          )}
        </form>
      ) : (
        <h1>No hay registros</h1>
      )}
    </div>
  )
}

export default Home
