import React from 'react'
import '../styles/Popup.css'

const Popup = (props) => {
  const handleClose = () => {
    const updatedPopup = {}
    updatedPopup['isLoading'] = false

    props.setTrigger({
      ...props.trigger,
      ...updatedPopup
    })
  }

  const deleteExercise = async (id) => {
    await fetch(`${API_URL}/exercises/${id}`, {
      method: 'DELETE'
    })
  }

  return (props.trigger) ? (
      <div className='popup'>
          <div className='popup-inner'>
              <p>¿Está seguro que desea eliminar el ejercicio: {props.trigger.exercise.fullName}?</p>

              <div className='group-btn'>
                <button className='close-btn' onClick={() => handleClose()} >
                  Cancelar
                </button>

                <button className='confirm-btn' onClick={() => deleteExercise(props.trigger.exercise.id)}>
                  Eliminar
                </button>
              </div>
          </div>
      </div>
    ) : ''
}

export default Popup
