import React, { useEffect, useState } from 'react'
import '../styles/NewSession.css'

const NewSession = ({API_URL, onShow, session, setSession}) => {
  const [exercisesSession, setExercisesSession] = useState([])
  const getExercisesSession = async (sessionId) => {
    const res = await fetch(`${API_URL}/exercisesSessions/${sessionId}`)
    const data = await res.json()

    setExercisesSession(data)
  }

  useEffect(() => {
    getExercisesSession(1)
  }, [API_URL])

  const onSubmit = (e) => {
    e.preventDefault()

    //addSession(session)
  }

  const handleSession = (key, value) => {
    const updatedSession = {}
    updatedSession[key] = value

    setSession({
      ...session,
      ...updatedSession
    })
  }

  return (
    <div className='form-container'>
      <form onSubmit={onSubmit}>
        <div className='form-body'>
          <input 
            required={true}
            type='text'
            placeholder='Nombre'
            value={session.name}
            onChange={(e) => handleSession('name', e.target.value)}
          />

          <textarea
            className='description-field'
            placeholder='Descripción'
            type='text'
            value={session.description}
            onChange={(e) => handleSession('description', e.target.value)}
          />

          {exercisesSession.map(exerciseSession => (
            <div className="card text-center">
              <div className="card-header">
                <ul className="nav nav-tabs card-header-tabs">
                  <li className="nav-item">
                    <h5 className="nav-link active" style={{fontWeight:'bold'}}>{exerciseSession.name}</h5>
                  </li>
                </ul>
              </div>
              <div className="card-body">
                <div>

                </div>

                <div>
                  <div className="card-row">
                    <label>Series:</label>
                    <input 
                      required={true}
                      type='number'
                      value={session.series}
                      onChange={(e) => handleSession('series', e.target.value)}
                    />
                  </div>

                  <div className="card-row">
                    <label>Reps:</label>
                    <input 
                      required={true}
                      type='number'
                      value={session.minReps}
                      onChange={(e) => handleSession('minReps', e.target.value)}
                    />
                    <label>-</label>
                    <input 
                      required={true}
                      type='number'
                      value={session.maxReps}
                      onChange={(e) => handleSession('maxReps', e.target.value)}
                    />
                  </div>

                  <div className="card-row">
                    <label>RIR:</label>
                    <input 
                      required={true}
                      type='number'
                      value={session.RIR}
                      onChange={(e) => handleSession('RIR', e.target.value)}
                    />
                  </div>

                  <div className="card-row">
                    <label>Descanso:</label>
                    <input 
                      required={true}
                      type='number'
                      value={session.rest}
                      onChange={(e) => handleSession('rest', e.target.value)}
                    />
                  </div>
                </div>

                <div>

                </div>
              </div>
            </div>
          ))}
        </div>
      </form>      
    </div>
  )
}

export default NewSession
