import React, { useState } from 'react'
import Header from './Header'
import '../styles/NewExercise.css'
import { Navigate } from 'react-router-dom'

const NewExercise = ({API_URL, onShow, exercise, setExercise}) => {
  const [mainMuscle, setMainMuscle] = useState({
    name: '',
    type: 'Principal',
    exerciseId: 0
  })

  const [secondMuscle, setSecondMuscle] = useState({
    name: '',
    type: 'Secundario',
    exerciseId: 0
  })

  const muscleList = [
    {id: 1, name:'Bíceps'},
    {id: 2, name:'Core'},
    {id: 3, name:'Cuádriceps'},
    {id: 4, name:'Deltoides'},
    {id: 5, name:'Espalda'},
    {id: 6, name:'Gemelo y sóleo'},
    {id: 7, name:'Glúteo'},
    {id: 8, name:'Isquio'},
    {id: 9, name:'Pectoral'},
    {id: 10, name:'Tríceps'}
  ]
  const [mainList, setMainList] = useState(muscleList)
  const [secondList, setSecondList] = useState(muscleList)

  const handleExercise = (key, value) => {
    const updatedExercise = {}
    updatedExercise[key] = value

    let union = ''

    if(key === 'name') {
      if(exercise.equipment === 'barra' || exercise.equipment === 'máquina') {
        union = ' en '
      } else if(exercise.equipment === '') {
        union = ''
      } else {
        union = ' con '
      }
      updatedExercise.fullName = value + union + exercise.equipment
    }

    if(key === 'equipment') {
      if(value === 'barra' || value === 'máquina') {
        union = ' en '
      } else if(value === '') {
        union = ''
      } else {
        union = ' con '
      }

      updatedExercise.fullName = exercise.name + union + value
    }

    setExercise({
      ...exercise,
      ...updatedExercise
    })
  }

  const handleMuscle = (type, value) => {
    const updatedExercise = {}
    updatedExercise[type] = value

    const updatedList = muscleList.filter(muscle => muscle.name !== value)

    setExercise(
      ...exercise,
      ...updatedExercise
    )
    if(type === 'mainMuscle') {
      setSecondList(updatedList)
    } else {
      setMainList(updatedList)
    }
  }

  const addMuscle = async (muscle) => {
    await fetch(`${API_URL}/muscles`, {
      method: 'POST',
      headers: {
        'Content-type' : 'application/json'
      },
      body: JSON.stringify(muscle)
    }) 
  }

  const addExercise = async (exercise) => {
    await fetch(`${API_URL}/exercises`, {
      method: 'POST',
      headers: {
        'Content-type' : 'application/json'
      },
      body: JSON.stringify(exercise)
    }).then(response => response.json()).then(data => {
      const updatedMainMuscle = {
        name: mainMuscle.name,
        type: 'Principal',
        exerciseId: data.id
      }
      addMuscle(updatedMainMuscle)

      const updatedSecondMuscle = {
        name: secondMuscle.name,
        type: 'Secundario',
        exerciseId: data.id
      }
      addMuscle(updatedSecondMuscle)
    }).then(setTimeout(onShow, 1000))
  }

  const onSubmit = (e) => {
    e.preventDefault()

    addExercise(exercise)
  }

  return (
    <div className='form-container'>
      <form onSubmit={onSubmit}>
        <div className='form-body'>
          <input 
            required={true}
            type='text'
            placeholder='Nombre'
            value={exercise.name}
            onChange={(e) => handleExercise('name', e.target.value)}
          />

          <div className='section-adjacent'>
            <label>Material:</label>
            <select defaultValue={exercise.equipment} onChange={(e) => handleExercise('equipment', e.target.value)}>
              <option value='' defaultValue disabled>Seleccione</option>
              <option value='barra'>Barra</option>
              <option value='barra olímpica'>Barra olímpica</option>
              <option value='barra hexagonal'>Barra hexagonal</option>
              <option value='barra Z'>Barra Z</option>
              <option value='goma elástica'>Goma elástica</option>
              <option value='kettlebell'>Kettlebell</option>
              <option value='mancuernas'>Mancuernas</option>
              <option value='máquina'>Máquina</option>
              <option value='polea'>Polea</option>
              <option value=''>Sin material</option>
            </select>
          </div>

          <div className='muscle-section'>
            <label className='label-bold'>Grupos musculares:</label>

            <div className='muscle-group'>
              <div className='muscle'>
                <label>Principal</label>
                <select defaultValue={mainMuscle.name} onChange={(e) => handleMuscle('Principal', e.target.value)}>
                  <option value='' defaultValue disabled>Seleccione</option>
                  {mainList.map((group) => (
                    <option key={group.id} value={group.name}>{group.name}</option>
                  ))}
                </select>
              </div>

              <div className='muscle'>
                <label>Secundario</label>
                <select defaultValue={secondMuscle.name} onChange={(e) => handleMuscle('Secundario', e.target.value)}>
                  <option value='' defaultValue disabled>Seleccione</option>
                  {secondList.map((group) => (
                    <option key={group.id} value={group.name}>{group.name}</option>
                  ))}
                </select>
              </div>
            </div>
          </div>

          <input
            type='file'
            value={exercise.video}
            onChange={(e) => handleExercise('video', e.target.value)}
          />

          <textarea
            className='description-field'
            placeholder='Descripción'
            type='text'
            value={exercise.description}
            onChange={(e) => handleExercise('description', e.target.value)}
          />

          <div className='submit-button'>
            <input
              type='submit'
              value='Guardar ejercicio'
            />
          </div>
        </div>
      </form>
    </div>
  )
}

export default NewExercise
