import React, { useEffect, useState } from 'react'

const MySessions = ({API_URL}) => {
  const [upcoming, setUpcoming] = useState([])
  const [undated, setUndated] = useState([])
  const [previous, setPrevious] = useState([])
  const actualDate = new Date()

  const getSessions = async () => {
    await fetch(`${API_URL}/sessions`)
          .then(res => res.json())
          .then(data => {
            console.log(data)
            data.forEach(se => {
              if(se.date === null) {
                console.log('1')
                let updated = [...undated, se]
                setUndated(updated)
              } else if(se.date < actualDate) {
                let updated = [...previous, se]
                setPrevious(updated)
              } else {
                let updated = [...upcoming, se]
                setUpcoming(updated)
              }
            })
          })
  }

  useEffect(() => {
    getSessions()
  }, [API_URL])

  return (
    <div>
      <h3>Pr√≥ximas</h3>
      {upcoming.map(uc => {
        return <p key={uc.id}>{uc.name}</p>
      })}
      <h3>Sin programar</h3>
      {undated.map(ud => {
        return <p key={ud.id}>{ud.name}</p>
      })}
      <h3>Anteriores</h3>
      {previous.map(pv => {
        return <p key={pv.id}>{pv.name}</p>
      })}
    </div>
  )
}

export default MySessions
