import { Grid } from '@mui/material'
import React, { useEffect, useState } from 'react'
import '../styles/NewRoutine.css'

const NewRoutine = ({ API_URL, onShow, routine, setRoutine, microcycles, setMicrocycles, sessionsDay, setSessionsDay }) => {
  const microcycleDays = [
    {
      value: 0,
      label: 'Lunes'
    },
    {
      value: 1,
      label: 'Martes'
    },
    {
      value: 2,
      label: 'Miércoles'
    },
    {
      value: 3,
      label: 'Jueves'
    },
    {
      value: 4,
      label: 'Viernes'
    },
    {
      value: 5,
      label: 'Sábado'
    },
    {
      value: 6,
      label: 'Domingo'
    },
    {
      value: 7,
      label: 'Lunes'
    },
    {
      value: 8,
      label: 'Martes'
    },
    {
      value: 9,
      label: 'Miércoles'
    },
    {
      value: 10,
      label: 'Jueves'
    },
    {
      value: 11,
      label: 'Viernes'
    },
    {
      value: 12,
      label: 'Sábado'
    },
    {
      value: 13,
      label: 'Domingo'
    },
  ];

  const handleRoutine = (key, value) => {
    let updated = {...routine}
    updated[key] = value
    console.log(updated)
    setRoutine(updated)
  }

  const onSubmit = (e) => {
    e.preventDefault()

    console.log('Envía')
  }

  return (
    <div className='ru-form-container'>
      <form onSubmit={onSubmit}>
        <div className='ru-form'>
          <div className='microcycle-container'>
            <h2>Microciclo 1</h2>

            {microcycles.map(day => {
              return (
                <div className='row-content'>
                  <label>Duración</label>
                  <select defaultValue={0} onChange={(e) => handleRoutine('length', e.target.value)}>
                    <option defaultValue value={0} disabled>Seleccione</option>
                  </select>
                </div>
              )
            })}

          </div>

          <input
            type='submit'
            value='Guardar'
            />
        </div>
      </form>
    </div>
  )
}

export default NewRoutine
