import { useState, useEffect } from "react"

const Home = ({API_URL}) => {
  const [petitions, setPetitions] = useState([])

  const getPetitions = async () => {
    await fetch(`${API_URL}/petitions`)
          .then(res => res.json())
          .then(data => {
            setPetitions(data.filter(f => f.receiver == localStorage.getItem('id') && f.status == 'pending'))
          })
  }

  useEffect(() => {
    getPetitions()
  }, [API_URL])

  return (
    <div>
      {petitions.length > 0 ? (
        <div>
          <h2>Peticiones pendientes</h2>
          {petitions.map(petition => {
            return  <label key={petition.id} className="adjacent" >
                      <div className="">
                        {petition.senderName}
                        <span className="d-block small">
                          {petition.senderUserName} 
                        </span>
                      </div>
                      <div className='actions'>
                        <button>
                          Aceptar
                        </button>

                        <br />

                        <button>
                          Rechazar
                        </button>
                      </div>
                    </label>
          })}
        </div>
      ) : (
        <></>
      )}
    </div>
  )
}

export default Home
