import { Grid } from '@mui/material'
import React, { useEffect, useState } from 'react'
import '../styles/NewRoutine.css'

const NewRoutine = ({ API_URL, onShow, routine, setRoutine, microcycles, setMicrocycles, sessionsDay, setSessionsDay }) => {
  const emptyMicrocycle = useState(
    {
      number: 1
    }
  )

  const emptySession = useState(
    {
      day: 'Martes',
      microcycleId: 0
    }
  )

  const addSession = (microcycle) => {
    let empty = emptySession
    empty.microcycleId = microcycle

    setSessionsDay([
      ...sessionsDay,
      empty
    ])
  }

  const addMicrocycle = () => {
    let empty = emptyMicrocycle
    empty.number = microcycles.length + 1

    setMicrocycles([
      ...microcycles,
      empty
    ])
  }

  useEffect(() => {
    console.log('')
  }, [routine, microcycles, sessionsDay])

  return (
    <div>
      <input 
        type='text'
        placeholder='Nombre'
      />

      <input 
        type='text'
        placeholder='Descripción'
      />

      <Grid container rowSpacing={3} columnSpacing={0}>
        {microcycles.map((microcycle, index) => (
          <Grid key={microcycle.name} item xs={4} className='center-label'>
            <div className='microcycle-container'>
              <h5>Microciclo {microcycle.number}</h5>

              {sessionsDay.filter((sessionDay) => sessionDay.microcycleId === microcycle.number)
                .map(session => (
                  <div className='session-row'>
                    {session.day}
                  </div>
                ))
              }

              <div className='session-row'>
                <button onClick={() => addSession(microcycle.number)}>
                  Añadir
                </button>
              </div>
            </div>
          </Grid>
        ))}

        <Grid item xs={4} className='center-label'>
          <button onClick={() => addMicrocycle()}>
            Añadir
          </button>
        </Grid>
      </Grid>
    </div>
  )
}

export default NewRoutine
