import React, { useEffect, useState } from 'react'

const MySessions = ({API_URL}) => {
  const [upcoming, setUpcoming] = useState([])
  const [undated, setUndated] = useState([])
  const [previous, setPrevious] = useState([])
  const actualDate = new Date()

  const getSessions = async () => {
    await fetch(`${API_URL}/sessions`)
          .then(res => res.json())
          .then(data => {
            console.log(actualDate < new Date(data[0].date))
            setUpcoming(data.filter(uc => uc.date >= actualDate))
            setUndated(data.filter(un => un.date === null))
            setPrevious(data.filter(pv => pv.date < actualDate))
          })
  }

  useEffect(() => {
    getSessions()
  }, [API_URL])

  return (
    <div>
      <h3>Pr√≥ximas</h3>
      {upcoming.map(uc => {
        return <p key={uc.id}>{uc.name}</p>
      })}
      <h3>Sin programar</h3>
      {undated.map(ud => {
        return <p key={ud.id}>{ud.name}</p>
      })}
      <h3>Anteriores</h3>
      {previous.map(pv => {
        return <p key={pv.id}>{pv.name}</p>
      })}
    </div>
  )
}

export default MySessions
