import React, { useEffect, useState } from 'react'
import Popup from './Popup'

const Routines = ({API_URL}) => {
  const [routines, setRoutines] = useState([])
  const [show, setShow] = useState(false)
  const [edit, setEdit] = useEffect(false)
  const [buttonPopup, setButtonPopup] = useState({
    isLoading: false,
    record: {}
  })

  const getRoutines = async (name) => {
    const res = await fetch(`${API_URL}/routines/${name}`)
    const data = await res.json()

    setRoutines(data)
  }

  const onShow = () => {
    setShow(!show)
    if(edit) {
      setEdit(false)
    }
  }

  useEffect(() => {
    getRoutines('')
  }, [API_URL])

  return (
    <div className='page-body'>
      <h1>Rutinas</h1>

      <button onClick={() => onShow()}>
        {show ? 'Cancelar' : 'Crear rutina'}
      </button>

      <br />

      {buttonPopup.isLoading ? (
        <Popup API_URL={API_URL} trigger={buttonPopup} setTrigger={setButtonPopup} type={'rutina'} />
      ) : (
        <></>
      )}

    </div>
  )
}

export default Routines
