import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import { Grid } from '@mui/material'
import '../styles/Exercises.css'
import Header from './Header'
import NewExercise from './NewExercise'
import Popup from './Popup'

const Exercises = ({ API_URL }) => {
  const [exercises, setExercises] = useState([])
  const [searchTerm, setSearchTerm] = useState('')
  const [muscles, setMuscles] = useState([])
  const [show, setShow] = useState(false)
  const [buttonPopup, setButtonPopup] = useState(false)

  const [emptyExercise, setEmptyExercise] = useState({
    name: '',
    equipment: '',
    fullName: '',
    description: '',
    createdBy: 1,
    video: ''
  })

  const getExercises = async (name) => {
    const res = await fetch(`${API_URL}/exercises/${name}`)
    const data = await res.json()

    setExercises(data)
    exercises.map(ex => console.log(ex))
  }

  const getMuscles = async () => {
    const res = await fetch(`${API_URL}/muscles`)
    const data = await res.json()

    setMuscles(data)
  }

  useEffect(() => {
    getExercises('')
    getMuscles()
  }, [API_URL, show])

  const handleChange = (search) => {
    setSearchTerm(search)
    getExercises(search)
  }

  const onDelete = async (id) => {
    await fetch(`${API_URL}/exercises/${id}`, {
      method: 'DELETE'
    })

    setExercises(exercises.filter((exercise) => exercise.id !== id))
    setButtonPopup(false)
  }

  const onShow = () => {
    setShow(!show)
  }

  return (
    <div className='page-body'>
      {/*<Header placeholder='ejercicio' title='Ejercicios' path='new-exercise' btn={true} />*/}

      <h1>Ejercicios</h1>

      <button onClick={() => onShow()}>
        {show ? 'Cancelar' : 'Crear ejercicio'}
      </button>

      <br />

      {show ? (
        <>
          <NewExercise 
            API_URL={API_URL} 
            onShow={onShow} 
            exercises={exercises}
            exercise={emptyExercise}
            setExercise={setEmptyExercise}
          />
        </>
      ) : (
        <>
          <div className='search'>
            <input
              className='search-box'
              placeholder='Busca un ejercicio'
              value={searchTerm}
              onChange={(e) => handleChange(e.target.value)}
            />
          </div>

          {exercises?.length > 0 ? (
              <Grid container rowSpacing={3} columnSpacing={0}>
                  {exercises.map(exercise => (
                    <Grid key={exercise.id} item xs={4} className='center-label'>
                      <label key={exercise.id} className="adjacent" >
                        <div className="">
                          <Link to={`/exercise/${exercise.id}`} className='link' >
                            {exercise.fullName}
                          </Link>
                          <span className="d-block small opacity-50">
                            {muscles.filter(muscle => muscle.exerciseId === exercise.id).map((m, index, array) => (
                              index === array.length -1 ? `${m.name}` : `${m.name}, `
                            ))}
                          </span>
                        </div>
                        <div className='actions'>
                          <svg 
                            onClick={() => {}}
                            xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="pencil bi bi-pencil-square" viewBox="0 0 16 16">
                            <path d="M15.502 1.94a.5.5 0 0 1 0 .706L14.459 3.69l-2-2L13.502.646a.5.5 0 0 1 .707 0l1.293 1.293zm-1.75 2.456-2-2L4.939 9.21a.5.5 0 0 0-.121.196l-.805 2.414a.25.25 0 0 0 .316.316l2.414-.805a.5.5 0 0 0 .196-.12l6.813-6.814z"/>
                            <path fillRule="evenodd" d="M1 13.5A1.5 1.5 0 0 0 2.5 15h11a1.5 1.5 0 0 0 1.5-1.5v-6a.5.5 0 0 0-1 0v6a.5.5 0 0 1-.5.5h-11a.5.5 0 0 1-.5-.5v-11a.5.5 0 0 1 .5-.5H9a.5.5 0 0 0 0-1H2.5A1.5 1.5 0 0 0 1 2.5v11z"/>
                          </svg>
                          <br />
                          <svg 
                            onClick={() => onDelete(exercise.id)}
                            xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="trash bi bi-trash3-fill" viewBox="0 0 16 16">
                            <path d="M11 1.5v1h3.5a.5.5 0 0 1 0 1h-.538l-.853 10.66A2 2 0 0 1 11.115 16h-6.23a2 2 0 0 1-1.994-1.84L2.038 3.5H1.5a.5.5 0 0 1 0-1H5v-1A1.5 1.5 0 0 1 6.5 0h3A1.5 1.5 0 0 1 11 1.5Zm-5 0v1h4v-1a.5.5 0 0 0-.5-.5h-3a.5.5 0 0 0-.5.5ZM4.5 5.029l.5 8.5a.5.5 0 1 0 .998-.06l-.5-8.5a.5.5 0 1 0-.998.06Zm6.53-.528a.5.5 0 0 0-.528.47l-.5 8.5a.5.5 0 0 0 .998.058l.5-8.5a.5.5 0 0 0-.47-.528ZM8 4.5a.5.5 0 0 0-.5.5v8.5a.5.5 0 0 0 1 0V5a.5.5 0 0 0-.5-.5Z"/>
                          </svg>
                        </div>
                      </label>

                      <Popup trigger={buttonPopup} setTrigger={setButtonPopup} onDelete={() => deleteExercise(exercise.id)} >
                        <p>¿Está seguro que desea eliminar el ejercicio: {exercise.fullName}?</p>
                      </Popup>
                    </Grid>
                  ))}
              </Grid>
            ) : (
              <h1>
                No se han encontrado ejercicios
              </h1>
            )
          }
        </>
      )}
    </div>
  )
}

export default Exercises
