import React, { useEffect, useState } from 'react'
import '../styles/NewSession.css'

const NewSession = ({API_URL, onShow, session, setSession}) => {
  const [exercisesSession, setExercisesSession] = useState([])
  const getExercisesSession = async (sessionId) => {
    const res = await fetch(`${API_URL}/exercisesSessions/${sessionId}`)
    const data = await res.json()

    setExercisesSession(data)
  }

  useEffect(() => {
    getExercisesSession(1)
  }, [API_URL])

  const onSubmit = (e) => {
    e.preventDefault()

    //addSession(session)
  }

  const handleSession = (key, value) => {
    const updatedSession = {}
    updatedSession[key] = value

    setSession({
      ...session,
      ...updatedSession
    })
  }

  return (
    <div className='form-container'>
      <form onSubmit={onSubmit}>
        <div className='form-body'>
          <input 
            required={true}
            type='text'
            placeholder='Nombre'
            value={session.name}
            onChange={(e) => handleSession('name', e.target.value)}
          />

          <textarea
            className='description-field'
            placeholder='Descripción'
            type='text'
            value={session.description}
            onChange={(e) => handleSession('description', e.target.value)}
          />

          <div className="card text-center">
            <div className="card-header">
              <ul className="nav nav-tabs card-header-tabs">
                <li className="nav-item">
                  <p className="nav-link active">Press banca inclinado</p>
                </li>
                <li className="nav-item">
                  <p className="nav-link">Curl de bíceps con mancuernas</p>
                </li>
                <li className="nav-item">
                  <p className="nav-link">Cinta 30 min.</p>
                </li>
              </ul>
            </div>
            <div className="card-body">
              <h5 className="card-title">Special title treatment</h5>
              <p className="card-text">Series: 5</p>
              <p className="card-text">Reps.: 10-12</p>
              <p className="card-text">RIR: 1-2</p>
              <p className="card-text">Descanso: 30"</p>
            </div>
          </div>
        </div>
      </form>      
    </div>
  )
}

export default NewSession
