        <>
          <div className='search'>
            <input
              className='search-box'
              placeholder='Busca una rutina'
              value={searchTerm}
              onChange={(e) => handleChange(e.target.value)}
            />
          </div>
        </>
      )}

    </div>
  )
}

export default Routines
