import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import '../styles/Exercises.css'
import Header from './Header'

const Exercises = ({ API_URL }) => {
  const [exercises, setExercises] = useState([])
  const [searchTerm, setSearchTerm] = useState('')
  const [muscles, setMuscles] = useState([])

  const getExercises = async (name) => {
    const res = await fetch(`${API_URL}/exercises/${name}`)
    const data = await res.json()

    setExercises(data)
    exercises.forEach(ex => console.log(ex))
  }

  const getMuscles = async () => {
    const res = await fetch(`${API_URL}/muscles`)
    const data = await res.json()

    setMuscles(data)
  }

  useEffect(() => {
    getExercises('')
    getMuscles()
  }, [API_URL])

  const handleChange = (search) => {
    setSearchTerm(search)
    getExercises(search)
  }

  const onDelete = async (id) => {
    await fetch(`${API_URL}/exercises/${id}`, {
      method: 'DELETE'
    })

    setExercises(exercises.filter((exercise) => exercise.id !== id))
  }

  return (
    <div className='page-body'>
      <Header placeholder='ejercicio' title='Ejercicios' path='new-exercise' />

      <div className='search'>
        <input
          className='search-box'
          placeholder='Busca un ejercicio'
          value={searchTerm}
          onChange={(e) => handleChange(e.target.value)}
        />
      </div>

      {exercises?.length > 0 ? (
        <div className="list-group list-group-checkable d-grid gap-2 border-0 w-auto">
          {
            exercises.map(exercise => (
              <label key={exercise.id} className="list-group-item rounded-3 py-3 adjacent" >
                <div className="">
                  <Link to={`/exercise/${exercise.id}`} className='link' >
                    {exercise.fullName}
                  </Link>
                  <span className="d-block small opacity-50">
                    {muscles.filter(muscle => muscle.exerciseId === exercise.id).map((m, index, array) => (
                      index === array.length -1 ? `${m.name}` : `${m.name}, `
                    ))}
                  </span>
                </div>
                <svg 
                  onClick={() => onDelete(exercise.id)}
                  xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="trash bi bi-trash3-fill" viewBox="0 0 16 16">
                  <path d="M11 1.5v1h3.5a.5.5 0 0 1 0 1h-.538l-.853 10.66A2 2 0 0 1 11.115 16h-6.23a2 2 0 0 1-1.994-1.84L2.038 3.5H1.5a.5.5 0 0 1 0-1H5v-1A1.5 1.5 0 0 1 6.5 0h3A1.5 1.5 0 0 1 11 1.5Zm-5 0v1h4v-1a.5.5 0 0 0-.5-.5h-3a.5.5 0 0 0-.5.5ZM4.5 5.029l.5 8.5a.5.5 0 1 0 .998-.06l-.5-8.5a.5.5 0 1 0-.998.06Zm6.53-.528a.5.5 0 0 0-.528.47l-.5 8.5a.5.5 0 0 0 .998.058l.5-8.5a.5.5 0 0 0-.47-.528ZM8 4.5a.5.5 0 0 0-.5.5v8.5a.5.5 0 0 0 1 0V5a.5.5 0 0 0-.5-.5Z"/>
                </svg>
              </label>
            ))
          }
        </div>
        ) : (
          <h1>
            No se han encontrado ejercicios
          </h1>
        )
      }
    </div>
  )
}

export default Exercises
