  const handleRoutine = (key, value) => {
    let updated = {...routine}
    updated[key] = value
    console.log(updated)
    setRoutine(updated)
  }

  const onSubmit = (e) => {
    e.preventDefault()

    console.log('Envía')
  }



  return (
    <div className='ru-form-container'>
      <form onSubmit={onSubmit}>
        <div className='ru-form'>
          <Grid container rowSpacing={3} columnSpacing={0}>
            {microcycles.map(day => {
              return (
                <Grid key={day.number} item xs={4} className='center-label'>
                  <div key={day.number} className='microcycle-container'>
                    <h2>Microciclo {day.number}</h2>

                    <div className='row-content'>
                      <label>Duración</label>
                      <select>
                        <option defaultValue value={0} disabled>Seleccione</option>
                      </select>
                    </div>
                  </div>
                </Grid>
              )
            })}
          </Grid>

          <input
            type='submit'
            value='Guardar'
            />
        </div>
      </form>
    </div>
  )
}

export default NewRoutine
