import { Grid } from '@mui/material'
import React, { useEffect, useState } from 'react'
import '../styles/NewRoutine.css'

const NewRoutine = ({ API_URL, onShow, routine, setRoutine, microcycles, setMicrocycles, sessionsDay, setSessionsDay }) => {
  const emptyMicrocycle =
    {
      number: 1
    }

  const emptySession =
    {
      day: 'Martes',
      microcycleId: 0
    }

  const addSession = (microcycle) => {
    let empty = {...emptySession}
    empty.microcycleId = microcycle

    setSessionsDay([
      ...sessionsDay,
      empty
    ])
  }

  const addMicrocycle = () => {
    let empty = {...emptyMicrocycle}
    empty.number = microcycles.length + 1

    setMicrocycles([
      ...microcycles,
      empty
    ])

    addSession(empty.number)
  }

  const handleSession = (key, value, index) => {
    let updated = sessionsDay
    updated[index][key] = value

    setSessionsDay([
      ...sessionsDay,
      ...updated
    ])
  }

  useEffect(() => {
    console.log(microcycles)
    console.log(sessionsDay)
  }, [routine, microcycles, sessionsDay])

  return (
    <div className='ru-form-container'>
      <input 
        type='text'
        placeholder='Nombre'
      />

      <input 
        type='text'
        placeholder='Descripción'
      />

      <Grid key={routine.id} container rowSpacing={3} columnSpacing={2}>
        {microcycles.map((microcycle, index) => (
          <Grid key={index} item xs={6} className='center-label'>
            <div className='microcycle-container'>
              <h5>Microciclo {microcycle.number}</h5>

              {sessionsDay.filter((sessionDay) => sessionDay.microcycleId === microcycle.number)
                .map((session, index) => (
                  <div key={session.id} className='session-row'>
                    <select defaultValue={sessionsDay[index].day} onChange={(e) => handleSession('equipment', e.target.value, index)}>
                      <option value='' defaultValue disabled>Seleccione</option>
                    </select>

                    <select defaultValue={sessionsDay[index].session} onChange={(e) => handleSession('equipment', e.target.value, index)}>
                      <option value='' defaultValue disabled>{Seleccione}</option>
                    </select>

                    <button onClick={() => addSession(microcycle.number)}>
                      {index}
                    </button>
                  </div>
                ))
              }

              <div className='session-row'>

              </div>
            </div>
          </Grid>
        ))}

        <Grid item xs={6} className='center-label'>
          <div className='add-microcycle'>
            <button onClick={() => addMicrocycle()}>
              Añadir
            </button>
          </div>
        </Grid>
      </Grid>
    </div>
  )
}

export default NewRoutine
