import React, { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom'
import '../styles/Routine.css'

const Routine = ({API_URL}) => {
  const params = useParams()
  const [routine, setRoutine] = useState({})
  const [sessionsRoutines, setSessionsRoutines] = useState([])
  const [sessionMap, setSessionMap] = useState({})
  const weekDays = [
    'Lunes',
    'Martes',
    'Miercoles',
    'Jueves',
    'Viernes',
    'Sábado',
    'Domingo'
  ]

  useEffect(() => {
    const getRoutine = async () => {
      await fetch(`${API_URL}/routines`)
      .then(res => res.json())
      .then(data => {
        data.map(r => {
          if(r.id == params.id) {
            setRoutine(r)
          }
        })
      })
    }

    const getSessionsRoutines = async () => {
      fetch(`${API_URL}/sessionsRoutines/${params.id}`)
      .then(res => res.json())
      .then(data => {
        data.map(s => {
          if(s.day < 7) {
            sessionMap[s.day] = {
              id: s.sessionId,
              name: s.name
            }
          }
        })
        setSessionsRoutines(data)
      })
    }

    getRoutine()
    getSessionsRoutines()
  }, [API_URL, params.id])

  return (
    <div className='routine-container'>
      <h1>{routine.name}</h1>
      <table className='routine-detail'>
        <thead>
          <tr>
            {weekDays.map(weekDay => {
              return <th key={weekDay} className='routine-detail'>{weekDay}</th> 
            })}
          </tr>
        </thead>
        <tbody>
          <tr>
            {weekDays.map((weekDay, index) => {
              return (
                <td className='routine-detail' key={index}> 
                  {sessionMap[index] ? (
                    <Link to={`/session/${sessionMap[index] ? sessionMap[index].id : ''}`}>
                      {sessionMap[index] ? sessionMap[index].name : ''}
                    </Link>
                    ) : (
                      'Descanso'
                    )
                  }
                </td>
              )
            })}
          </tr>
        </tbody>
      </table>

      <div className='info-card'>
        <div className='info-column'>
          <div className='info-row'>
            <label style={{fontWeight: 'bold'}}>Duración: </label>
            ' ' {routine.length} semanas
          </div>

          <div className='info-row'>
            <label>Fecha de comienzo: </label>
            <></>
          </div>
        </div>

        <div className='info-column'>
          <div className='info-row'>
            <label>¿Finalizada?: </label>
            <></>
          </div>

          <div className='info-row'>
            <label>Asignación: </label>
            <></>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Routine
