import React, { useEffect } from 'react'
import { useState } from 'react'
import '../styles/Customers.css'
import ShowCustomers from './ShowCustomers'

const Customers = ({API_URL}) => {
  const [customers, setCustomers] = useState([])
  const [filtered, setFiltered] = useState([])
  const [showMine, setShowMine] = useState(false)

  useEffect(() => {
    const getCustomers = async () => {
      await fetch(`${API_URL}/users`)
            .then(res => res.json())
            .then(data => {
              setCustomers(data.filter(d => d.trainer != localStorage.getItem('id')))
              setFiltered(data.filter(d => d.trainer == localStorage.getItem('id')))
            })
    }

    getCustomers()
  }, [API_URL])

  return (
    <div className='customers-container'>
      <div className='cutomers-options'>
        <h1 className={`customer-title ${showMine ? 'title-selected' : ''}`} onClick={() => setShowMine(true)}>Mis clientes</h1>
        <h1>|</h1>
        <h1 className={`customer-title ${showMine ? '' : 'title-selected'}`} onClick={() => setShowMine(false)}>Todos</h1>
      </div>

      {showMine ? (
        (filtered.length > 0 ? (
          console.log(filtered),
          <ShowCustomers customers={filtered}
          />
        ) : (
          <h1>
            No se han encontrado sesiones
          </h1>
        ))
      ) : (
        (customers.length > 0 ? (
          console.log(customers),
          <ShowCustomers customers={customers}
          />
        ) : (
          <h1>
            No se han encontrado sesiones
          </h1>
        ))
      )}
    </div>
  )
}

export default Customers
