import { Grid } from '@mui/material'
import React, { useEffect, useState } from 'react'
import '../styles/NewRoutine.css'

const NewRoutine = ({ API_URL, onShow, routine, setRoutine, sessions, edit }) => {
  const [weekDays, setWeekDays] = useState([
    {
      name: 'Lunes',
      sessionId: ''
    }, 
    {
      name: 'Martes',
      sessionId: ''
    }, 
    {
      name: 'Miércoles',
      sessionId: ''
    },  
    {
      name: 'Jueves',
      sessionId: ''
    },  
    {
      name: 'Viernes',
      sessionId: ''
    },  
    {
      name: 'Sábado',
      sessionId: ''
    },  
    {
      name: 'Domingo',
      sessionId: ''
    } 
  ])

  const handleRoutine = (key, value) => {
    let updated = {...routine}
    updated[key] = value

    setRoutine(updated)
  }

  const handleWeekDays = (value, i) => {
    let updated = [...weekDays]
    updated[i].sessionId = value

    setWeekDays(updated)
  }

  const onSubmit = (e) => {
    e.preventDefault()

    console.log('Envía')
  }  

  useEffect(() => {
    console.log(weekDays)
  }, [weekDays])

  return (
    <div className='ru-form-container'>
      <form onSubmit={onSubmit}>
        <div className='ru-form'>
          <div className='row-section'>
            <input 
              type='text'
              size='75'
              required={true}
              placeholder='Nombre'
              value={routine.name}
              onChange={(e) => handleRoutine('name', e.target.value)}
            />

            <textarea
              className='description-field'
              placeholder='Descripción'
              type='text'
              value={routine.description}
              onChange={(e) => handleRoutine('description', e.target.value)}
            />
          </div>

          <table>
            <thead>
              <tr>
                {weekDays?.map(day => {
                  return (
                    <th key={day.name}>{day.name}</th>
                  )
                })}
              </tr>
            </thead>
            <tbody>
              <tr>
                {weekDays?.map((day, i) => {
                  return (
                    <td key={day.name}>
                      <select value={day.routineId} onChange={(e) => handleWeekDays(e.target.value, i)}>
                        <option value=''>-</option>
                        {sessions?.map(session => {
                          return (
                            <option key={session.id}>{session.name}</option>
                          )
                        })}
                      </select>
                    </td>
                  )
                })}
              </tr>
            </tbody>
          </table>

          <div className='submit-button'>
            <input
              type='submit'
              value='Guardar'
              />
          </div> 
        </div>
      </form>
    </div>
  )
}

export default NewRoutine
