import userEvent from '@testing-library/user-event'
import React from 'react'
import { useEffect } from 'react'
import { useState } from 'react'
import { useNavigate, useParams } from 'react-router-dom'
import '../styles/Profile.css'

const Profile = ({API_URL}) => {
  const params = useParams()
  const navigate = useNavigate()
  const [user, setUser] = useState({})
  const [newEmail, setNewEmail] = useState('')
  const [confirmEmail, setConfirmEmail] = useState('')
  const [newPassword, setNewPassword] = useState('')
  const [confirmPassword, setConfirmPassword] = useState('')
  const [showPassword, setShowPassword] = useState(false)
  const [showConfirmPassword, setShowConfirmPassword] = useState(false)
  const [emailError, setEmailError] = useState(false)
  const [passwordError, setPasswordError] = useState(false)

  const handleUser = (key, value) => {
    let updated = {...user}
    updated[key] = value

    setUser(updated)
  }

  useEffect(() => {
    const getProfile = async () => {
      await fetch(`${API_URL}/users`)
            .then(res => res.json())
            .then(data => {
              data.map(u => {
                if(u.id == localStorage.getItem('id')) {
                  setUser(u)
                }
              })
            })
    }

    getProfile()
  }, [API_URL])

  const checkEmail = () => {
    if(newEmail == confirmEmail) {
      return true
    } else {
      setEmailError(true)
      return false
    }
  }

  const checkPassword = () => {
    if(newPassword == confirmPassword) {
      return true
    } else {
      setPasswordError(true)
      return false
    }
  }

  const updateUser = async () => {
    await fetch(`${API_URL}/users/${params.id}`, {
      method: 'PUT',
      headers: {
        'Content-type' : 'application/json'
      },
      body: JSON.stringify(user)
    })
  }

  const onSubmit = (e) => {
    e.preventDefault()

    if(checkEmail() && checkPassword()) {
      updateUser()
    }
  }

  return (
    <div className='text-center'>
      <button onClick={() => navigate(-1)} className='cancel-button space-top'>
        Volver
      </button>

      {params.id == localStorage.getItem('id') ? (
        <div className='pf-form-container'>
          <h2>Perfil</h2>
          <form onSubmit={onSubmit}>
            <div className='profile-form'>
              <div className='profile-row'>
                <label>Nombre:</label>
                <input 
                  type='text'
                  value={user.firstName}
                  onChange={(e) => handleUser('firstName', e.target.value)}
                  className='profile-input'
                />
              </div>

            </div>

            <div className='submit-button space-above'>
                <input
                  type='submit'
                  value='Guardar cambios'
                  className='create-button'
                />
              </div>
          </form>
        </div>
      ) : (
        <h1>Vaya... parece que te has equivocado de ruta</h1>
      )}
    </div>
  )
}

export default Profile
