import React from 'react'
import { useState, useEffect } from 'react'
import Popup from './Popup'

const Sessions = ({API_URL}) => {
  const [sessions, setSessions] = useState([])
  const [searchTerm, setSearchTerm] = useState('')
  const [show, setShow] = useState(false)
  const [edit, setEdit] = useState(false)
  const [buttonPopup, setButtonPopup] = useState({
    isLoading: false,
    record: {}
  })

  const [emptySession, setEmptySession] = useState({
    name: '',
    description: ''
  })

  const [editSession, setEditSession] = useState({})

  const getSessions = async (name) => {
    const res = await fetch(`${API_URL}/sessions/${name}`)
    const data = await res.json()

    setSessions(data)
    sessions.map(s => console.log(s))
  }

  useEffect(() => {
    getSessions('')
  }, [API_URL, show])

  const handleChange = (search) => {
    setSearchTerm(search)
    getSessions(search)
  }

  const handleEdit = (session) => {
    setEditSession(session)
    setShow(true)
    setEdit(true)
  }

  const handleDelete = (session) => {
    setButtonPopup({
      isLoading: true,
      record: session
    })
  }

  const onShow = () => {
    setShow(!show)
    if(edit) {
      setEdit(false)
    }
  }

  return (
    <div className='page-body'>
      <h1>Sesiones</h1>

      <button onClick={() => onShow()}>
        'Crear sesión'
      </button>

      <br />

      {buttonPopup.isLoading ? (
        <Popup API_URL={API_URL} trigger={buttonPopup} setTrigger={setButtonPopup} type={'sesión'} />
      ) : (
        <></>
      )}
    </div>
  )
}

export default Sessions
