import { Grid } from '@mui/material'
import React, { useEffect, useState } from 'react'
import '../styles/NewRoutine.css'

const NewRoutine = ({ API_URL, onShow, routine, setRoutine, sessions, weekDays, setWeekDays, edit }) => {
  const emptyWeekDays = [
    {
      name: 'Lunes',
      day: 0,
      sessionId: ''
    }, 
    {
      name: 'Martes',
      day: 1,
      sessionId: ''
    }, 
    {
      name: 'Miércoles',
      day: 2,
      sessionId: ''
    },  
    {
      name: 'Jueves',
      day: 3,
      sessionId: ''
    },  
    {
      name: 'Viernes',
      day: 4,
      sessionId: ''
    },  
    {
      name: 'Sábado',
      day: 5,
      sessionId: ''
    },  
    {
      name: 'Domingo',
      day: 6,
      sessionId: ''
    } 
  ]

  //const [weekDays, setWeekDays] = useState([...emptyWeekDays])

  const emptyRoutine = {
    name: '',
    description: '',
    length: '',
    createdBy: 1
  }

  const handleRoutine = (key, value) => {
    let updated = {...routine}
    updated[key] = value

    setRoutine(updated)
  }

  const handleWeekDays = (value, i) => {
    let updated = [...weekDays]
    updated[i].sessionId = value

    setWeekDays(updated)
  }

  const addRoutine = async () => {
    await fetch(`${API_URL}/routines`, {
      method: 'POST',
      headers: {
        'Content-type' : 'application/json'
      },
      body: JSON.stringify(routine)
    })
    .then(res => 
      res.json())
    .then(data => {
      createSessionsRoutines(data.id)
    })
  }

  const updateRoutine = async () => {
    await fetch(`${API_URL}/routines/${routine.id}`, {
      method: 'PUT',
      headers: {
        'Content-type' : 'application/json'
      },
      body: JSON.stringify(routine)
    })
    .then(res => 
      res.json())
    .then(data => {
      createSessionsRoutines(data.id)
    })
  }

  const createSessionsRoutines = (routineId) => {
    let sessionsRoutines = []
    for(let i=0; i<routine.length; i++) {
      weekDays.forEach(day => {
        let sr = {
          weekDay: day.name,
          day: day.day + (7*i),
          sessionId: day.sessionId,
          routineId: routineId
        }

        sessionsRoutines.push(sr)
      })
    }

    if(edit) {
      updateSessionsRoutines(sessionsRoutines)
    } else {
      addSessionsRoutines(sessionsRoutines)
    } 
  }

  const addSessionsRoutines = async (sessionsRoutines) => {
    sessionsRoutines.forEach(sr => {
      if(sr.sessionId !== '') {
        fetch(`${API_URL}/sessionsRoutines`, {
          method: 'POST',
          headers: {
            'Content-type' : 'application/json'
          },
          body: JSON.stringify(sr)
        })
      }
    })

    setRoutine(emptyRoutine)
    setWeekDays(emptyWeekDays)
    setTimeout(onShow, 1000)
  }

  const updateSessionsRoutines = async (sessionsRoutines) => {
    sessionsRoutines.forEach(sr => {
      if(sr.sessionId !== '') {
        fetch(`${API_URL}/sessionsRoutines/${sr.id}`, {
          method: 'PUT',
          headers: {
            'Content-type' : 'application/json'
          },
          body: JSON.stringify(sr)
        })
      }
    })

    setRoutine(emptyRoutine)
    setWeekDays(emptyWeekDays)
    setTimeout(onShow, 1000)
  }

  const onSubmit = (e) => {
    e.preventDefault()

    if(edit) {
      updateRoutine()
    } else {
      addRoutine()
    }

    console.log('Envía')
  }  

  useEffect(() => {
    console.log(weekDays)
    console.log(routine)
  }, [weekDays, routine])

  return (
    <div className='ru-form-container'>
      <form onSubmit={onSubmit}>
        <div className='ru-form'>
          <div className="info-section">
            <div className='column'>
              <div className="left-column">
                <input 
                  type='text'
                  size='26'
                  required={true}
                  placeholder='Nombre'
                  value={routine.name}
                  onChange={(e) => handleRoutine('name', e.target.value)}
                />

                <textarea
                  className='description-field'
                  placeholder='Descripción'
                  type='text'
                  value={routine.description}
                  onChange={(e) => handleRoutine('description', e.target.value)}
                />

                <select required value={routine.length} onChange={(e) => handleRoutine('length', e.target.value)} >
                  <option value='' disabled>Duración</option>
                  <option value={1}>1 semana</option>
                  <option value={2}>2 semanas</option>
                  <option value={3}>3 semanas</option>
                  <option value={0}>1 mes</option>
                  <option value={-1}>1 trimestre</option>
                </select>

                <div>
                  <label>Fecha de inicio:</label>
                  <input 
                    type='date' 
                    value={routine.startDate}
                    onChange={(e) => handleRoutine('startDate', e.target.value)}
                  />
                </div>
              </div>
            </div>

            <div className='sessions'>
              <h4>Sesiones</h4>

              {weekDays?.map((day, i) => {
                return (
                  <div key={i} className='day-session'>
                    <label>{day.name}</label>

                    <select value={day.sessionId} onChange={(e) => handleWeekDays(e.target.value, i)}>
                      <option value=''>-</option>
                      {sessions?.map(session => {
                        return (
                          <option key={session.id} value={session.id}>{session.name}</option>
                        )
                      })}
                    </select>
                  </div>
                )
              })}
            </div>
          </div>

          <div className='submit-button'>
            <input
              type='submit'
              value='Guardar'
              />
          </div> 
        </div>
      </form>
    </div>
  )
}

export default NewRoutine
