import React, { useState, useEffect } from 'react'
import Header from './Header'
import '../styles/NewExercise.css'
import { Navigate } from 'react-router-dom'

const NewExercise = ({API_URL}) => {
  const [exercise, setExercise] = useState({
    name: '',
    equipment: '',
    fullName: '',
    description: '',
    createdBy: 1,
    video: ''
  })

  const [exerciseId, setExerciseId] = useState()

  useEffect(() => {
    console.log(exercise)
  }, [exercise])

  const handleChange = (key, value) => {
    const updatedExercise = {}
    updatedExercise[key] = value

    let union = ''

    if(key === 'name') {
      if(exercise.equipment === 'barra' || exercise.equipment === 'máquina') {
        union = ' en '
      } else if(exercise.equipment === '') {
        union = ''
      } else {
        union = ' con '
      }
      updatedExercise.fullName = value + union + exercise.equipment
    }

    if(key === 'equipment') {
      if(value === 'barra' || value === 'máquina') {
        union = ' en '
      } else if(value === '') {
        union = ''
      } else {
        union = ' con '
      }

      updatedExercise.fullName = exercise.name + union + value
    }

    setExercise({
      ...exercise,
      ...updatedExercise
    })
  }

  const addExercise = async (exercise) => {
    const res = await fetch(`${API_URL}/exercises`, {
      method: 'POST',
      headers: {
        'Content-type' : 'application/json'
      },
      body: JSON.stringify(exercise)
    })

    if(res.ok) {
      const data = await res.json()
      setExerciseId(data.id)
      (<Navigate to='exercises' />)
    } else {
      alert('Error: No se ha podido guardar el ejercicio')
    }
  }

  const onSubmit = (e) => {
    e.preventDefault()

    addExercise(exercise)
  }

  return (
    <div className='page-body'>
      <Header placeholder='Volver' title='Nuevo ejercicio' path='/exercises' />

      <div className='form-container'>
        <form onSubmit={onSubmit}>
          <div className='form-body'>
            <input 
              required={true}
              type='text'
              placeholder='Nombre'
              value={exercise.name}
              onChange={(e) => handleChange('name', e.target.value)}
            />

            <select defaultValue={exercise.equipment} onChange={(e) => handleChange('equipment', e.target.value)}>
              <option value='' defaultValue disabled>Seleccione</option>
              <option value='barra'>Barra</option>
              <option value='barra olímpica'>Barra olímpica</option>
              <option value='barra hexagonal'>Barra hexagonal</option>
              <option value='barra Z'>Barra Z</option>
              <option value='goma elástica'>Goma elástica</option>
              <option value='kettlebell'>Kettlebell</option>
              <option value='mancuernas'>Mancuernas</option>
              <option value='máquina'>Máquina</option>
              <option value='polea'>Polea</option>
              <option value=''>Sin material</option>
            </select>

            <div>
              <div>
                <label>Principal</label>
              </div>

              <div>
                <label>Secundario</label>
              </div>
            </div>

            <input
              type='file'
              value={exercise.video}
              onChange={(e) => handleChange('video', e.target.value)}
            />

            <textarea
              className='description-field'
              placeholder='Descripción'
              type='text'
              value={exercise.description}
              onChange={(e) => handleChange('description', e.target.value)}
            />

            <div className='submit-button'>
              <input
                type='submit'
                value='Guardar ejercicio'
              />
            </div>
          </div>
        </form>
      </div>
    </div>
  )
}

export default NewExercise
