import React, { useEffect, useState } from 'react'
import '../styles/NewSession.css'

const NewSession = ({API_URL, onShow, session, setSession, exercisesSession, setExercisesSession, exercises}) => {
  const [index, setIndex] = useState(0)
  const [emptyExercise, setEmptyExercise] = useState(
    {
      exerciseId: 0,
      name: '',
      series: 0,
      minReps: 0,
      maxReps: 0,
      rest: 0,
      RIR: 0,
      observations: ''
    }
  )

  const slidesContainer = document.getElementById("slides-container");
  const slide = document.querySelector(".slide");
  const prevButton = document.getElementById("slide-arrow-prev");
  const nextButton = document.getElementById("slide-arrow-next");

  nextButton.addEventListener("click", () => {
    const slideWidth = slide.clientWidth;
    slidesContainer.scrollLeft += slideWidth;
  });

  prevButton.addEventListener("click", () => {
    const slideWidth = slide.clientWidth;
    slidesContainer.scrollLeft -= slideWidth;
  });

  const addSession = async () => {
    await fetch(`${API_URL}/sessions`, {
      method: 'POST',
      headers: {
        'Content-type' : 'application/json'
      },
      body: JSON.stringify(session)
    })
    .then(res => res.json())
    .then(data => {
      exercisesSession.map(ex => ex.sessionId = data.id)

      fetch(`${API_URL}/exercisesSession`, {
        method: 'POST',
        headers: {
          'Content-type' : 'application/json'
        },
        body: JSON.stringify(exercisesSession)
      })
    })
  }

  const onSubmit = (e) => {
    e.preventDefault()

    addSession(session)
  }

  const handleSession = (key, value) => {
    const updatedSession = {}
    updatedSession[key] = value

    setSession({
      ...session,
      ...updatedSession
    })
  }

  const handleExerciseSession = (key, value) => {
    let updatedExercisesSession = exercisesSession
    let exercise = updatedExercisesSession[index]
    exercise[key] = value

    setExercisesSession(updatedExercisesSession)

    console.log(exercisesSession)
  }

  const handleAdd = () => {
    setIndex(index+1)
    setExercisesSession([
      ...exercisesSession,
      emptyExercise
    ])
  }

  useEffect(() => {
    console.log(exercisesSession)
  }, [index, setIndex, exercisesSession])

  return (
    <div className='form-container'>
      <form onSubmit={onSubmit}>
        <div className='form-body'>
          <input 
            required={true}
            type='text'
            placeholder='Nombre'
            value={session.name}
            onChange={(e) => handleSession('name', e.target.value)}
          />

          <textarea
            className='description-field'
            placeholder='Descripción'
            type='text'
            value={session.description}
            onChange={(e) => handleSession('description', e.target.value)}
          />

          <div className="card text-center">
            <div className="card-header">
              <ul className="nav nav-tabs card-header-tabs">
                <li className="nav-item">
                  <h5 className="nav-link active" style={{fontWeight:'bold'}}>Ejercicios</h5>
                </li>
              </ul>
            </div>
            <div className="card-body">
              <div className='card-button'>
                {index > 0 ? (
                  <svg onClick={() => setIndex(index-1)} xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-caret-left-fill" viewBox="0 0 16 16">
                    <path d="m3.86 8.753 5.482 4.796c.646.566 1.658.106 1.658-.753V3.204a1 1 0 0 0-1.659-.753l-5.48 4.796a1 1 0 0 0 0 1.506z"/>
                  </svg>
                ) :
                (
                  <></>
                )}
              </div>

              <div className='card-info'>
                <select required={true} defaultValue={exercisesSession[index].name} onChange={(e) => handleExerciseSession('name', e.target.value)}>
                  <option value='' defaultValue disabled>Seleccione</option>
                  {exercises.map(exercise => (
                    <option key={exercise.id} value={exercise.fullName}>{exercise.fullName}</option>
                  ))}
                </select>
                <div className="card-row">
                  <label>Series:</label>
                  <input 
                    required={true}
                    min='1'
                    type='number'
                    defaultValue={exercisesSession[index].series}
                    onChange={(e) => handleExerciseSession('series', e.target.value)}
                  />
                </div>

                <div className="card-row">
                  <label>Reps:</label>
                  <input 
                    required={true}
                    min='1'
                    type='number'
                    value={exercisesSession[index].minReps}
                    onChange={(e) => handleExerciseSession('minReps', e.target.value)}
                  />
                  <label>-</label>
                  <input 
                    required={true}
                    min='0'
                    type='number'
                    value={exercisesSession[index].maxReps}
                    onChange={(e) => handleExerciseSession('maxReps', e.target.value)}
                  />
                </div>

                <div className="card-row">
                  <label>RIR:</label>
                  <input 
                    required={true}
                    min='0'
                    type='number'
                    value={exercisesSession[index].RIR}
                    onChange={(e) => handleExerciseSession('RIR', e.target.value)}
                  />
                </div>

                <div className="card-row">
                  <label>Descanso:</label>
                  <input 
                    required={true}
                    min='0'
                    type='number'
                    value={exercisesSession[index].rest}
                    onChange={(e) => handleExerciseSession('rest', e.target.value)}
                  />
                </div>

                <div className="index-number">
                  <label className='index'>{index+1}</label>
                </div>
              </div>

              <div className='card-button'>
                {exercisesSession.length - index === 1 ? (
                  <svg id="slide-arrow-prev" xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-plus-circle" viewBox="0 0 16 16">
                    <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14zm0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16z"/>
                    <path d="M8 4a.5.5 0 0 1 .5.5v3h3a.5.5 0 0 1 0 1h-3v3a.5.5 0 0 1-1 0v-3h-3a.5.5 0 0 1 0-1h3v-3A.5.5 0 0 1 8 4z"/>
                  </svg>
                ) :
                (
                  <svg id="slide-arrow-next" xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-caret-right-fill" viewBox="0 0 16 16">
                    <path d="m12.14 8.753-5.482 4.796c-.646.566-1.658.106-1.658-.753V3.204a1 1 0 0 1 1.659-.753l5.48 4.796a1 1 0 0 1 0 1.506z"/>
                  </svg>
                )}
              </div>
            </div>
          </div>

          <div className='submit-session'>
            <input
              type='submit'
              value='Guardar sesión'
            />
          </div>
        </div>
      </form>      
    </div>
  )
}

export default NewSession
